<script>
import VueApexCharts from "vue-apexcharts";
import DashboardService from "@/services/dashboard.service";
import {
  BContainer,
  BOverlay,
  BCard,
  BRow,
  BCol,
  BAlert,
  BModal,
} from "bootstrap-vue";
import SchoolYearService from "@/services/SchoolYear.service";
export default {
  name: "Dashboard",
  components: {
    apexchart: VueApexCharts,
    BContainer,
    BOverlay,
    BCard,
    BRow,
    BCol,
    BAlert,
    BModal,
  },
  created() {
    this.employees();
    this.students();
    SchoolYearService.IsSchoolReadyForActiveSchoolYear().then((res) => {
      this.isreadyschoolyear = res.data.result;
    });
    this.show = false;
  },
  methods: {
    students() {
      DashboardService.StudentsCountInSchool(true).then((res) => {
        (this.StudentSeries = [
          {
            name: "O`g`il bolalar",
            data: res.data.result.map((item) => item.CountOfBoys),
          },
          {
            name: "Qiz bolalar",
            data: res.data.result.map((item) => item.CountOfGirls),
          },
          {
            name: "Jami",
            data: res.data.result.map((item) => item.CountOFStudents),
          },
        ]),
          (this.StudentchartOptions = {
            chart: {
              type: "bar",
              height: 350,
            },
            dataLabels: {
              enabled: true,
              formatter: function(val) {
                return val;
              },
              offsetY: 0,
              style: {
                fontSize: "12px",
                colors: ["#000"],
              },
            },
            plotOptions: {
              bar: {
                horizontal: false,
                columnWidth: "55%",
                endingShape: "rounded",
              },
            },
            stroke: {
              show: true,
              width: 2,
              colors: ["transparent"],
            },
            xaxis: {
              categories: res.data.result.map((x) => x.schoolgradename),
            },
            yaxis: {
              title: {
                text: "Jami o`quvchilar",
              },
            },
            fill: {
              opacity: 1,
            },
            tooltip: {
              y: {
                formatter: function(val) {
                  return val + " ta";
                },
              },
            },
          });
      });
    },
    employees() {
      DashboardService.EmployeesCountInSchool(true).then((res) => {
        (this.EmployeeSeries = [
          {
            name: "Jami",
            data: res.data.result.map((item) => item.employeecount),
          },
        ]),
          (this.EmployeechartOptions = {
            chart: {
              type: "bar",
              height: 350,
            },
            dataLabels: {
              enabled: true,
              formatter: function(val) {
                return val;
              },
              offsetX: 0,
              style: {
                fontSize: "12px",
                colors: ["#000"],
              },
            },
            plotOptions: {
              bar: {
                borderRadius: 4,
                horizontal: true,
              },
            },
            fill: {
              opacity: 1,
            },
            xaxis: {
              categories: res.data.result.map(
                (item) => item.staffcategoryfullname
              ),
            },
          });
      });
    },
    OpenReadySchoolModal() {
      this.ReadySchoolModal = true;
    },
  },
  data() {
    return {
      show: true,
      StudentSeries: [],
      StudentchartOptions: {},
      EmployeeSeries: [],
      EmployeechartOptions: {},
      isreadyschoolyear: false,
      ReadySchoolModal: false,
    };
  },
};
</script>
